  73
  74function formatPercent(value: number): string {
  75  return `${(Math.max(0, Math.min(1, value)) * 100).toFixed(1)}%`;
  76}
  77
  78const PRIVACY_STAGE_META: Record<string, { label: string; help: string }> = {
  79  raw_conversation: {
  80    label: "Original conversation text",
  81    help: "Privacy score pass rate when auditing the original conversation text.",
  82  },
  83  facet_summary: {
  84    label: "Facet summary",
  85    help: "Pass rate after reducing conversations into structured facet summaries.",
  86  },
  87  cluster_summary: {
  88    label: "Cluster summary",
  89    help: "Pass rate for cluster-level summaries used for aggregate insights.",
  90  },
  91};
  92
  93const EVAL_REPRESENTATION_META: Record<
  94  string,
  95  { label: string; help: string }
  96> = {
  97  privacy_summary: {
  98    label: "Privacy-safe summary",
  99    help: "Topic classification quality using privacy-filtered summaries.",
 100  },
 101  non_private_summary: {
 102    label: "Detailed summary (unfiltered)",
 103    help: "Topic classification quality using summaries before privacy filtering.",
 104  },
 105  raw_user_text: {
 106    label: "Raw user text",
 107    help: "Topic classification quality directly from original user text.",
 108  },
 109};
 110
 111const EVAL_METRIC_META: Record<string, { label: string; help: string }> = {
 112  accuracy: {
 113    label: "Topic match accuracy",
 114    help: "Share of examples where predicted topic exactly matches ground truth.",
 115  },
 116  macroF1: {
 117    label: "Balanced F1",
 118    help: "F1 score averaged equally across topics, so rare topics count equally.",
 119  },
 120  weightedF1: {
 121    label: "Volume-weighted F1",
 122    help: "F1 score weighted by topic frequency, emphasizing common topics.",
 123  },
 124};
 125
 126function stageLabel(stage: string): string {
 127  return PRIVACY_STAGE_META[stage]?.label ?? stage.replace(/_/g, " ");
 128}
 129
 130function stageHelp(stage: string): string {
 131  return (
 132    PRIVACY_STAGE_META[stage]?.help || "Privacy pass-rate for this audit stage."
 133  );
 134}
 135
 136function evalRepresentationLabel(name: string): string {
 137  return EVAL_REPRESENTATION_META[name]?.label ?? name.replace(/_/g, " ");
 138}
 139
 140function evalRepresentationHelp(name: string): string {
 141  return (
 142    EVAL_REPRESENTATION_META[name]?.help ||
 143    "Evaluation representation used to classify synthetic conversations."
 144  );
 145}
