 173function levelPillClass(level: number): string {
 174  if (level <= 0) {
 175    return "border-emerald-300/70 bg-emerald-100/80 text-emerald-700";
 176  }
 177  if (level === 1) {
 178    return "border-sky-300/70 bg-sky-100/80 text-sky-700";
 179  }
 180  return "border-violet-300/70 bg-violet-100/80 text-violet-700";
 181}
 182
 183const ROOT_ACCENT_PALETTE = [
 184  "#2563eb",
 185  "#0d9488",
 186  "#16a34a",
 187  "#d97706",
 188  "#9333ea",
 189  "#dc2626",
 190  "#0891b2",
 191  "#4f46e5",
 192  "#65a30d",
 193  "#b45309",
 194  "#db2777",
 195  "#475569",
 196];
 197
 198function humanizeEnum(value: string): string {
 199  return value.replace(/_/g, " ");
 200}
 201
 202function normalizeProjectionMethod(projectionMethod: string | null): string {
 203  if (!projectionMethod || !projectionMethod.trim()) {
 204    return "2D projection";
 205  }
 206  return projectionMethod.trim().toUpperCase();
 207}
 208
 209function projectionAxisLabel(
 210  projectionMethod: string | null,
 211  axis: 1 | 2,
 212): string {
 213  const normalized = projectionMethod?.trim().toLowerCase() ?? "";
 214  if (normalized === "pca") {
 215    return `PCA component ${axis}`;
 216  }
 217  if (normalized.includes("umap")) {
 218    return `UMAP axis ${axis}`;
 219  }
 220  return `Projection axis ${axis}`;
 221}
 222
 223function formatProjectionValue(value: number): string {
 224  if (!Number.isFinite(value)) {
 225    return "n/a";
 226  }
 227  return value.toFixed(3);
 228}
 229
 230function projectionExplanation(projectionMethod: string | null): string {
 231  const method = normalizeProjectionMethod(projectionMethod);
 232  return `${method} compresses high-dimensional embeddings into 2D. Nearby points are semantically similar clusters.`;
 233}
