 291function formatShare(value: number): string {
 292  const pct = Math.max(0, Math.min(1, value)) * 100;
 293  return `${pct % 1 === 0 ? pct.toFixed(0) : pct.toFixed(1)}%`;
 294}
 295
 296function metadataPreview(value: unknown): string {
 297  if (typeof value === "string") {
 298    return value.length > 120 ? `${value.slice(0, 117)}...` : value;
 299  }
 300  if (typeof value === "number" || typeof value === "boolean") {
 301    return String(value);
 302  }
 303  if (value === null || value === undefined) {
 304    return "null";
 305  }
 306  try {
 307    const encoded = JSON.stringify(value);
 308    return encoded.length > 120 ? `${encoded.slice(0, 117)}...` : encoded;
 309  } catch {
 310    return "[unavailable]";
 311  }
 312}
 313
 314type FlatTableRow = {
 315  node: RunVisualHierarchyNode;
 316  depth: number;
 317  accentColor: string;
 318  shareOfParent: number;
 319  shareOfRun: number;
 320  hasChildren: boolean;
 321  isExpanded: boolean;
 322};
 323
 324export function RunVisualSummary({
 325  visuals,
 326  loading,
 327  error,
 328}: RunVisualSummaryProps) {
 329  const hasMap = Boolean(visuals?.map && visuals.map.points.length > 0);
 330  const hasHierarchy = Boolean(
 331    visuals?.hierarchy &&
 332      (visuals.hierarchy.topLevelClusters.length > 0 ||
 333        visuals.hierarchy.nodes.length > 0),
 334  );
 335  const hasPrivacy = Boolean(
 336    visuals?.privacy && visuals.privacy.stages.length > 0,
 337  );
 338  const hasEvaluation = Boolean(
 339    visuals?.evaluation && visuals.evaluation.ablations.length > 0,
 340  );
 341  const hasAny = hasMap || hasHierarchy || hasPrivacy || hasEvaluation;
 342
 343  const keptPoints = visuals?.map?.points.filter((point) => point.kept) ?? [];
 344  const filteredPoints =
 345    visuals?.map?.points.filter((point) => !point.kept) ?? [];
 346  const privacyStages =
 347    visuals?.privacy?.stages.map((stage) => ({
 348      ...stage,
 349      label: stageLabel(stage.stage),
 350      help: stageHelp(stage.stage),
 351    })) ?? [];
 352  const evaluationRows =
 353    visuals?.evaluation?.ablations.map((ablation) => ({
 354      ...ablation,
 355      displayName: evalRepresentationLabel(ablation.name),
 356      help: evalRepresentationHelp(ablation.name),
 357    })) ?? [];
 358  const [isHierarchyDialogOpen, setIsHierarchyDialogOpen] = useState(false);
 359  const hierarchyNodes = visuals?.hierarchy?.nodes ?? [];
 360  const nodeById = useMemo(
 361    () => new Map(hierarchyNodes.map((node) => [node.id, node])),
 362    [hierarchyNodes],
 363  );
 364  const childIdsByParent = useMemo(() => {
 365    const grouped = new Map<string, string[]>();
 366    for (const node of hierarchyNodes) {
 367      if (!node.parentId) {
 368        continue;
 369      }
 370      const current = grouped.get(node.parentId) ?? [];
 371      current.push(node.id);
 372      grouped.set(node.parentId, current);
 373    }
 374    return grouped;
 375  }, [hierarchyNodes]);
 376  const rootNodeIds = useMemo(() => {
 377    const configuredRoots = visuals?.hierarchy?.rootNodeIds ?? [];
 378    if (configuredRoots.length > 0) {
 379      return configuredRoots.filter((nodeId) => nodeById.has(nodeId));
 380    }
 381    return hierarchyNodes
 382      .filter((node) => node.parentId === null)
 383      .sort((a, b) => b.level - a.level)
 384      .map((node) => node.id);
 385  }, [hierarchyNodes, nodeById, visuals?.hierarchy?.rootNodeIds]);
 386  const [selectedHierarchyNodeId, setSelectedHierarchyNodeId] =
 387    useState<string>("");
 388  const [hierarchySearchQuery, setHierarchySearchQuery] = useState("");
 389  const [expandedNodeIds, setExpandedNodeIds] = useState<Set<string>>(
 390    () => new Set<string>(),
 391  );
 392  const [leafRecordsNodeId, setLeafRecordsNodeId] = useState<string>("");
 393  const [leafRecordsClusterId, setLeafRecordsClusterId] = useState<
 394    number | null
 395  >(null);
 396  const [leafRecordsPayload, setLeafRecordsPayload] =
 397    useState<RunClusterConversationsResponse | null>(null);
 398  const [leafRecordsLoading, setLeafRecordsLoading] = useState(false);
 399  const [leafRecordsError, setLeafRecordsError] = useState("");
 400  const [leafRecordSearch, setLeafRecordSearch] = useState("");
 401
 402  useEffect(() => {
 403    if (!isHierarchyDialogOpen) {
 404      return;
 405    }
 406    if (selectedHierarchyNodeId && nodeById.has(selectedHierarchyNodeId)) {
 407      return;
 408    }
 409    const fallbackId = rootNodeIds[0] ?? hierarchyNodes[0]?.id ?? "";
 410    setSelectedHierarchyNodeId(fallbackId);
 411  }, [
 412    hierarchyNodes,
 413    isHierarchyDialogOpen,
 414    nodeById,
 415    rootNodeIds,
 416    selectedHierarchyNodeId,
 417  ]);
 418
 419  const leafNodeByClusterId = useMemo(() => {
 420    const byClusterId = new Map<number, RunVisualHierarchyNode>();
 421    for (const node of hierarchyNodes) {
 422      if (node.level !== 0 || node.sourceClusterId === null) {
 423        continue;
 424      }
 425      byClusterId.set(node.sourceClusterId, node);
 426    }
 427    return byClusterId;
 428  }, [hierarchyNodes]);
 429  const normalizedHierarchySearchQuery = hierarchySearchQuery
 430    .trim()
 431    .toLowerCase();
 432  const openLeafRecords = (nodeId: string, clusterId: number) => {
 433    setLeafRecordsNodeId(nodeId);
 434    setLeafRecordsClusterId(clusterId);
 435    setLeafRecordSearch("");
 436  };
 437  const rootIdByNodeId = useMemo(() => {
 438    const cache = new Map<string, string>();
 439
 440    const resolveRoot = (nodeId: string): string => {
 441      const existing = cache.get(nodeId);
 442      if (existing) {
 443        return existing;
 444      }
 445      const node = nodeById.get(nodeId);
 446      if (!node) {
 447        return nodeId;
 448      }
 449      if (!node.parentId || !nodeById.has(node.parentId)) {
 450        cache.set(nodeId, node.id);
 451        return node.id;
 452      }
 453      const resolved = resolveRoot(node.parentId);
 454      cache.set(nodeId, resolved);
 455      return resolved;
 456    };
 457
 458    for (const node of hierarchyNodes) {
 459      resolveRoot(node.id);
 460    }
 461    return cache;
 462  }, [hierarchyNodes, nodeById]);
 463  const rootAccentById = useMemo(() => {
 464    const map = new Map<string, string>();
 465    rootNodeIds.forEach((rootId, index) => {
 466      map.set(rootId, ROOT_ACCENT_PALETTE[index % ROOT_ACCENT_PALETTE.length]);
 467    });
 468    return map;
 469  }, [rootNodeIds]);
 470  const nodeAccentById = useMemo(() => {
 471    const map = new Map<string, string>();
 472    for (const node of hierarchyNodes) {
 473      const rootId = rootIdByNodeId.get(node.id) ?? node.id;
 474      map.set(node.id, rootAccentById.get(rootId) ?? ROOT_ACCENT_PALETTE[0]);
 475    }
 476    return map;
 477  }, [hierarchyNodes, rootAccentById, rootIdByNodeId]);
 478  const hierarchyTotalSize = useMemo(() => {
 479    return rootNodeIds.reduce((total, rootId) => {
 480      return total + Math.max(0, nodeById.get(rootId)?.size ?? 0);
 481    }, 0);
 482  }, [nodeById, rootNodeIds]);
 483  const hierarchyMatchNodeIds = useMemo(() => {
 484    if (!normalizedHierarchySearchQuery) {
 485      return new Set<string>();
 486    }
 487    const matches = new Set<string>();
 488    for (const node of hierarchyNodes) {
 489      const haystack =
 490        `${node.name} ${node.description} ${node.id}`.toLowerCase();
 491      if (haystack.includes(normalizedHierarchySearchQuery)) {
 492        matches.add(node.id);
 493      }
 494    }
 495    return matches;
 496  }, [hierarchyNodes, normalizedHierarchySearchQuery]);
 497  const hierarchyVisibleNodeIds = useMemo(() => {
 498    if (hierarchyMatchNodeIds.size === 0) {
 499      return null;
 500    }
 501    const visible = new Set<string>();
 502    const includeDescendants = (nodeId: string) => {
 503      const stack = [nodeId];
 504      while (stack.length > 0) {
 505        const currentId = stack.pop();
 506        if (!currentId || visible.has(currentId)) {
 507          continue;
 508        }
 509        visible.add(currentId);
 510        const childIds = childIdsByParent.get(currentId) ?? [];
 511        childIds.forEach((childId) => {
 512          stack.push(childId);
 513        });
 514      }
 515    };
 516    hierarchyMatchNodeIds.forEach((matchId) => {
 517      includeDescendants(matchId);
 518      let cursor = nodeById.get(matchId) ?? null;
 519      while (cursor?.parentId && nodeById.has(cursor.parentId)) {
 520        visible.add(cursor.parentId);
 521        cursor = nodeById.get(cursor.parentId) ?? null;
 522      }
 523    });
 524    return visible;
 525  }, [childIdsByParent, hierarchyMatchNodeIds, nodeById]);
 526  const flatTableRows = useMemo<FlatTableRow[]>(() => {
 527    const rows: FlatTableRow[] = [];
 528    const isSearching = hierarchyVisibleNodeIds !== null;
 529
 530    const walk = (nodeId: string, depth: number) => {
 531      const node = nodeById.get(nodeId);
 532      if (!node) {
 533        return;
 534      }
 535      if (isSearching && !hierarchyVisibleNodeIds.has(nodeId)) {
 536        return;
 537      }
 538      const childIds = childIdsByParent.get(nodeId) ?? [];
 539      const visibleChildIds = isSearching
 540        ? childIds.filter((id) => hierarchyVisibleNodeIds.has(id))
 541        : childIds;
 542      const hasChildren = visibleChildIds.length > 0;
 543      const isExpanded = isSearching
 544        ? hasChildren
 545        : expandedNodeIds.has(nodeId);
 546      const parentSize =
 547        node.parentId && nodeById.has(node.parentId)
 548          ? Math.max(1, nodeById.get(node.parentId)?.size ?? 1)
 549          : Math.max(1, node.size);
 550
 551      rows.push({
 552        node,
 553        depth,
 554        accentColor: nodeAccentById.get(node.id) ?? ROOT_ACCENT_PALETTE[0],
 555        shareOfParent: node.size / parentSize,
 556        shareOfRun: hierarchyTotalSize > 0 ? node.size / hierarchyTotalSize : 0,
 557        hasChildren,
 558        isExpanded,
 559      });
 560
 561      if (isExpanded) {
 562        const sortedChildren = visibleChildIds
 563          .map((id) => nodeById.get(id))
 564          .filter((n): n is NonNullable<typeof n> => Boolean(n))
 565          .sort((a, b) => b.size - a.size);
 566        for (const child of sortedChildren) {
 567          walk(child.id, depth + 1);
 568        }
 569      }
 570    };
 571
 572    for (const rootId of rootNodeIds) {
 573      walk(rootId, 0);
 574    }
 575    return rows;
 576  }, [
 577    childIdsByParent,
 578    expandedNodeIds,
 579    hierarchyTotalSize,
 580    hierarchyVisibleNodeIds,
 581    nodeAccentById,
 582    nodeById,
 583    rootNodeIds,
 584  ]);
 585  const generatedHierarchyDepth =
 586    visuals?.hierarchy?.generatedLevels !== null &&
 587    visuals?.hierarchy?.generatedLevels !== undefined &&
 588    visuals.hierarchy.generatedLevels > 0
 589      ? visuals.hierarchy.generatedLevels
 590      : visuals?.hierarchy?.maxLevel !== null &&
 591          visuals?.hierarchy?.maxLevel !== undefined &&
 592          visuals.hierarchy.maxLevel >= 0
 593        ? visuals.hierarchy.maxLevel + 1
 594        : null;
 595  const requestedHierarchyLevels =
 596    visuals?.hierarchy?.requestedLevels !== null &&
 597    visuals?.hierarchy?.requestedLevels !== undefined &&
 598    visuals.hierarchy.requestedLevels >= 0
 599      ? visuals.hierarchy.requestedLevels
 600      : null;
 601  const hierarchyDepthPolicy = visuals?.hierarchy?.depthPolicy ?? null;
 602  const hierarchyDepthStopReason = visuals?.hierarchy?.depthStopReason ?? null;
 603  const hierarchyWhyNotDeeper = visuals?.hierarchy?.whyNotDeeper ?? null;
 604  useEffect(() => {
 605    if (!isHierarchyDialogOpen || rootNodeIds.length === 0) {
 606      return;
 607    }
 608    setExpandedNodeIds((current) => {
 609      if (current.size > 0) {
 610        return current;
 611      }
 612      const initial = new Set<string>();
 613      for (const rootId of rootNodeIds) {
 614        initial.add(rootId);
 615      }
 616      return initial;
 617    });
 618  }, [isHierarchyDialogOpen, rootNodeIds]);
 619
 620  useEffect(() => {
 621    if (leafRecordsClusterId === null || !visuals?.runId) {
 622      setLeafRecordsPayload(null);
 623      setLeafRecordsError("");
 624      setLeafRecordsLoading(false);
 625      return;
 626    }
 627    const controller = new AbortController();
 628    setLeafRecordsLoading(true);
 629    setLeafRecordsError("");
 630
 631    void fetch(
 632      `/api/runs/${encodeURIComponent(visuals.runId)}/clusters/${leafRecordsClusterId}/conversations`,
 633      {
 634        cache: "no-store",
 635        signal: controller.signal,
 636      },
 637    )
 638      .then(async (response) => {
 639        if (!response.ok) {
 640          const payload = (await response.json().catch(() => null)) as {
 641            error?: string;
 642          } | null;
 643          throw new Error(
 644            payload?.error ||
 645              `Failed to load cluster conversations (${response.status}).`,
 646          );
 647        }
 648        return (await response.json()) as RunClusterConversationsResponse;
 649      })
 650      .then((payload) => {
 651        if (!controller.signal.aborted) {
 652          setLeafRecordsPayload(payload);
 653        }
 654      })
 655      .catch((fetchError: unknown) => {
 656        if (controller.signal.aborted) {
 657          return;
 658        }
 659        setLeafRecordsError(
 660          fetchError instanceof Error
 661            ? fetchError.message
 662            : "Failed to load cluster conversations.",
 663        );
 664        setLeafRecordsPayload(null);
 665      })
 666      .finally(() => {
 667        if (!controller.signal.aborted) {
 668          setLeafRecordsLoading(false);
 669        }
 670      });
 671
 672    return () => {
 673      controller.abort();
 674    };
 675  }, [leafRecordsClusterId, visuals?.runId]);
 676
 677  const selectedLeafRecordsNode =
 678    leafRecordsNodeId && nodeById.has(leafRecordsNodeId)
 679      ? (nodeById.get(leafRecordsNodeId) ?? null)
 680      : leafRecordsClusterId !== null
 681        ? (leafNodeByClusterId.get(leafRecordsClusterId) ?? null)
 682        : null;
 683  const filteredLeafRecords = useMemo(() => {
 684    const rows = leafRecordsPayload?.records ?? [];
 685    const query = leafRecordSearch.trim().toLowerCase();
 686    if (!query) {
 687      return rows;
 688    }
 689    return rows.filter((row) => {
 690      const metadataHaystack = row.userMetadata
 691        ? Object.entries(row.userMetadata)
 692            .map(([key, value]) => `${key}:${metadataPreview(value)}`)
 693            .join(" ")
 694        : "";
 695      const facetSummary = row.facet?.summary ?? "";
 696      const facetTask = row.facet?.task ?? "";
 697      const facetLanguage = row.facet?.language ?? "";
 698      const haystack =
