 63function formatRelativeTime(value: string): string {
 64  if (!value) {
 65    return "n/a";
 66  }
 67  const timestamp = Date.parse(value);
 68  if (Number.isNaN(timestamp)) {
 69    return "n/a";
 70  }
 71  const diffSeconds = Math.round((timestamp - Date.now()) / 1000);
 72  const absDiff = Math.abs(diffSeconds);
 73  const formatter = new Intl.RelativeTimeFormat(undefined, {
 74    numeric: "auto",
 75  });
 76
 77  if (absDiff < 60) {
 78    return formatter.format(diffSeconds, "second");
 79  }
 80  if (absDiff < 3_600) {
 81    return formatter.format(Math.round(diffSeconds / 60), "minute");
 82  }
 83  if (absDiff < 86_400) {
 84    return formatter.format(Math.round(diffSeconds / 3_600), "hour");
 85  }
 86  return formatter.format(Math.round(diffSeconds / 86_400), "day");
 87}
