 89async function fetchJson<T>(url: string, signal?: AbortSignal): Promise<T> {
 90  const response = await fetch(url, { cache: "no-store", signal });
 91  if (!response.ok) {
 92    throw new Error(`Request failed (${response.status}) for ${url}`);
 93  }
 94  return (await response.json()) as T;
 95}
