    (acc, run) => {
      acc.total += 1;

      switch (run.state) {
        case "running":
          acc.running += 1;
          break;
        case "completed":
        case "completed_with_warnings":
          acc.completed += 1;
          break;
        case "failed":
          acc.failed += 1;
          break;
      }

      return acc;
    },
    { total: 0, running: 0, completed: 0, failed: 0 }
  );
}, [runsData]);

