 78  const [autoRefresh, setAutoRefresh] = useState<boolean>(false);
 79  const [showAllRuns, setShowAllRuns] = useState<boolean>(false);
 80
 81  const loadRuns = useCallback(
 82    async (signal?: AbortSignal, options?: { background?: boolean }) => {
 83      const background = options?.background ?? false;
 84      if (!background) {
 85        setLoadingRuns(true);
 86      }
 87      try {
 88        const payload = await fetchJson<RunListResponse>(
 89          "/api/runs?limit=80",
 90          signal,
 91        );
 92        setRunsData(payload);
 93        setRunsError("");
 94      } catch (error) {
 95        if (signal?.aborted) {
 96          return;
 97        }
 98        setRunsError(
 99          error instanceof Error ? error.message : "Failed to load runs.",
100        );
101      } finally {
102        if (!signal?.aborted && !background) {
103          setLoadingRuns(false);
104        }
105      }
106    },
107    [],
108  );
109
110  useEffect(() => {
111    const controller = new AbortController();
112    void loadRuns(controller.signal);
113    return () => {
114      controller.abort();
115    };
116  }, [loadRuns]);
117
118  useEffect(() => {
119    if (!autoRefresh) {
120      return;
121    }
122    const intervalId = window.setInterval(() => {
123      void loadRuns(undefined, { background: true });
124    }, RUN_LIST_REFRESH_INTERVAL_MS);
125    return () => {
126      window.clearInterval(intervalId);
127    };
128  }, [autoRefresh, loadRuns]);
129
130  const hasRuns = Boolean(runsData && runsData.runs.length > 0);
131
132  const runSnapshot = useMemo(() => {
133    if (!runsData) {
134      return { total: 0, running: 0, completed: 0, failed: 0 };
135    }
136
137    return runsData.runs.reduce(
138      (acc, run) => {
139        acc.total += 1;
140
141        switch (run.state) {
142          case "running":
143            acc.running += 1;
144            break;
145          case "completed":
146          case "completed_with_warnings":
147            acc.completed += 1;
148            break;
149          case "failed":
150            acc.failed += 1;
151            break;
152        }
153
154        return acc;
155      },
156      { total: 0, running: 0, completed: 0, failed: 0 },
157    );
158  }, [runsData]);
159
160  const handleRunStarted = useCallback(
161    (runId: string) => {
162      router.push(`/runs/${encodeURIComponent(runId)}`);
163    },
164    [router],
