31      } catch {
32        if (signal?.aborted) {
33          return;
34        }
35        setNotFound(true);
36      } finally {
37        if (!signal?.aborted) {
38          setChecking(false);
39        }
40      }
41    },
42    [runId],
43  );
44
45  useEffect(() => {
46    const controller = new AbortController();
47    void checkRunExists(controller.signal);
48    return () => {
49      controller.abort();
50    };
51  }, [checkRunExists]);
52
53  if (checking) {
54    return null;
55  }
56
57  if (notFound) {
58    return (
59      <div className="mx-auto max-w-xl px-4 py-16">
