 10const DEFAULT_OPTIONS: RunLaunchOptions = {
 11  withFacets: true,
 12  withClustering: true,
 13  withLabeling: true,
 14  withHierarchy: true,
 15  withPrivacy: true,
 16  withEval: true,
 17  streaming: false,
 18  streamChunkSize: 32,
 19  hierarchyLevels: 5,
 20  hierarchyDepthPolicy: "adaptive",
 21  clusterStrategy: "hybrid",
 22  clusterLeafMode: "auto",
 23  clusterTargetLeafSize: 25,
 24  clusterMinLeafClusters: 20,
 25  clusterMaxLeafClusters: 600,
 26  clusterHdbscanMinClusterSize: 12,
 27  clusterHdbscanMinSamples: 6,
 28  clusterNoisePolicy: "nearest",
 29  strict: false,
 30  limit: null,
 31  evalCount: null,
 32};
