 34type JsonRecord = Record<string, unknown>;
 35
 36function asRecord(value: unknown): JsonRecord | null {
 37  if (typeof value !== "object" || value === null || Array.isArray(value)) {
 38    return null;
 39  }
 40  return value as JsonRecord;
 41}
 42
 43function asBoolean(value: unknown, fallback: boolean): boolean {
 44  if (typeof value === "boolean") {
 45    return value;
 46  }
 47  if (typeof value === "string") {
 48    const normalized = value.trim().toLowerCase();
 49    if (normalized === "true" || normalized === "1" || normalized === "yes") {
 50      return true;
 51    }
 52    if (normalized === "false" || normalized === "0" || normalized === "no") {
 53      return false;
 54    }
 55  }
 56  return fallback;
 57}
 58
 59function asNullableInt(value: unknown): number | null {
 60  if (value === null || value === undefined || value === "") {
 61    return null;
 62  }
 63  const parsed = Number(value);
 64  if (!Number.isFinite(parsed)) {
 65    return null;
 66  }
 67  const normalized = Math.floor(parsed);
 68  return normalized > 0 ? normalized : null;
 69}
 70
 71function asIntInRange(
 72  value: unknown,
 73  bounds: { fallback: number; min: number; max: number },
 74): number {
 75  const { fallback, min, max } = bounds;
 76  const parsed = Number(value);
 77  if (!Number.isFinite(parsed)) {
 78    return fallback;
 79  }
 80  const normalized = Math.floor(parsed);
 81  if (normalized < min) {
 82    return min;
 83  }
 84  if (normalized > max) {
 85    return max;
 86  }
 87  return normalized;
 88}
 89
 90function asOneOf<T extends string>(
 91  value: unknown,
 92  allowed: readonly T[],
 93  fallback: T,
 94): T {
 95  if (typeof value !== "string") {
 96    return fallback;
 97  }
 98  const normalized = value.trim();
 99  return allowed.includes(normalized as T) ? (normalized as T) : fallback;
100}
101
102function parseOptions(
103  raw: FormDataEntryValue | null,
104): Partial<RunLaunchOptions> {
105  if (typeof raw !== "string" || !raw.trim()) {
106    return DEFAULT_OPTIONS;
107  }
108  try {
109    const parsed = asRecord(JSON.parse(raw));
110    if (!parsed) {
111      return DEFAULT_OPTIONS;
112    }
113    return {
114      withFacets: asBoolean(parsed.withFacets, DEFAULT_OPTIONS.withFacets),
115      withClustering: asBoolean(
116        parsed.withClustering,
117        DEFAULT_OPTIONS.withClustering,
118      ),
119      withLabeling: asBoolean(
120        parsed.withLabeling,
121        DEFAULT_OPTIONS.withLabeling,
122      ),
123      withHierarchy: asBoolean(
124        parsed.withHierarchy,
125        DEFAULT_OPTIONS.withHierarchy,
126      ),
127      withPrivacy: asBoolean(parsed.withPrivacy, DEFAULT_OPTIONS.withPrivacy),
128      withEval: asBoolean(parsed.withEval, DEFAULT_OPTIONS.withEval),
129      streaming: asBoolean(parsed.streaming, DEFAULT_OPTIONS.streaming),
130      streamChunkSize:
131        asNullableInt(parsed.streamChunkSize) ??
132        DEFAULT_OPTIONS.streamChunkSize,
133      hierarchyLevels: asIntInRange(parsed.hierarchyLevels, {
134        fallback: DEFAULT_OPTIONS.hierarchyLevels,
135        min: 2,
136        max: 20,
137      }),
138      hierarchyDepthPolicy: asOneOf(
139        parsed.hierarchyDepthPolicy,
140        ["adaptive", "strict_min"] as const,
141        DEFAULT_OPTIONS.hierarchyDepthPolicy,
142      ),
143      clusterStrategy: asOneOf(
144        parsed.clusterStrategy,
145        ["kmeans", "hdbscan", "hybrid"] as const,
146        DEFAULT_OPTIONS.clusterStrategy,
147      ),
148      clusterLeafMode: asOneOf(
149        parsed.clusterLeafMode,
150        ["fixed", "auto"] as const,
151        DEFAULT_OPTIONS.clusterLeafMode,
152      ),
153      clusterTargetLeafSize: asIntInRange(parsed.clusterTargetLeafSize, {
154        fallback: DEFAULT_OPTIONS.clusterTargetLeafSize,
155        min: 1,
156        max: 5000,
157      }),
158      clusterMinLeafClusters: asIntInRange(parsed.clusterMinLeafClusters, {
159        fallback: DEFAULT_OPTIONS.clusterMinLeafClusters,
160        min: 1,
161        max: 5000,
162      }),
163      clusterMaxLeafClusters: asIntInRange(parsed.clusterMaxLeafClusters, {
164        fallback: DEFAULT_OPTIONS.clusterMaxLeafClusters,
165        min: 1,
166        max: 10000,
167      }),
168      clusterHdbscanMinClusterSize: asIntInRange(
169        parsed.clusterHdbscanMinClusterSize,
170        {
171          fallback: DEFAULT_OPTIONS.clusterHdbscanMinClusterSize,
172          min: 2,
173          max: 5000,
174        },
175      ),
176      clusterHdbscanMinSamples: asIntInRange(parsed.clusterHdbscanMinSamples, {
177        fallback: DEFAULT_OPTIONS.clusterHdbscanMinSamples,
178        min: 1,
179        max: 2000,
180      }),
181      clusterNoisePolicy: asOneOf(
182        parsed.clusterNoisePolicy,
183        ["nearest", "singleton", "drop"] as const,
184        DEFAULT_OPTIONS.clusterNoisePolicy,
185      ),
186      strict: asBoolean(parsed.strict, DEFAULT_OPTIONS.strict),
187      limit: asNullableInt(parsed.limit),
188      evalCount: asNullableInt(parsed.evalCount),
189    };
190  } catch {
191    return DEFAULT_OPTIONS;
192  }
193}
