195function withNoStoreHeaders(
196  payload: RunLaunchResponse | { error: string },
197  status = 200,
198) {
199  return NextResponse.json(payload, {
200    status,
201    headers: {
202      "Cache-Control": "no-store, no-cache, must-revalidate, proxy-revalidate",
203      Pragma: "no-cache",
204      Expires: "0",
205    },
206  });
207}
