209export async function POST(
210  request: Request,
211): Promise<NextResponse<RunLaunchResponse | { error: string }>> {
212  try {
213    const formData = await request.formData();
214    const runsRoot = getRunsRootPath();
215    const requestedRunIdValue = formData.get("runId");
216    const requestedRunId =
217      typeof requestedRunIdValue === "string" ? requestedRunIdValue : "";
218    const options = parseOptions(formData.get("options"));
219
220    const uploaded = formData.get("file");
221    if (!(uploaded instanceof File) || uploaded.size <= 0) {
222      throw new Error("Upload a JSONL file before starting a run.");
223    }
224    const persisted = await persistUploadedInput(uploaded, runsRoot);
225
226    const job = await startBackgroundRun({
227      inputPath: persisted.inputPath,
228      requestedRunId,
229      options,
230      runsRoot,
231    });
232
233    return withNoStoreHeaders(
234      {
235        generatedAtUtc: new Date().toISOString(),
236        runsRoot,
237        job,
238      },
239      201,
240    );
241  } catch (error) {
242    return withNoStoreHeaders(
243      {
244        error: error instanceof Error ? error.message : "Failed to launch run.",
245      },
246      400,
247    );
248  }
249}
