276    runArgs.push("--cluster-leaf-mode", String(options.clusterLeafMode));
277    runArgs.push(
278      "--cluster-target-leaf-size",
279      String(options.clusterTargetLeafSize),
280    );
281    runArgs.push(
282      "--cluster-min-leaf-clusters",
283      String(options.clusterMinLeafClusters),
284    );
285    runArgs.push(
286      "--cluster-max-leaf-clusters",
287      String(options.clusterMaxLeafClusters),
288    );
289    runArgs.push(
290      "--cluster-hdbscan-min-cluster-size",
291      String(options.clusterHdbscanMinClusterSize),
292    );
293    runArgs.push(
294      "--cluster-hdbscan-min-samples",
295      String(options.clusterHdbscanMinSamples),
296    );
297    runArgs.push("--cluster-noise-policy", String(options.clusterNoisePolicy));
298  }
299  if (options.withLabeling) {
300    runArgs.push("--with-labeling");
301  }
302  if (options.withHierarchy) {
303    runArgs.push("--with-hierarchy");
304    runArgs.push("--hierarchy-levels", String(options.hierarchyLevels));
305    runArgs.push(
306      "--hierarchy-depth-policy",
307      String(options.hierarchyDepthPolicy),
308    );
309  }
310  if (options.withPrivacy) {
311    runArgs.push("--with-privacy");
312  }
313  if (options.withEval) {
314    runArgs.push("--with-eval");
315  }
316  if (options.limit !== null) {
317    runArgs.push("--limit", String(options.limit));
318  }
319  if (options.evalCount !== null) {
320    runArgs.push("--eval-count", String(options.evalCount));
321  }
322  if (options.streaming) {
323    runArgs.push(
324      "--streaming",
325      "--stream-chunk-size",
326      String(options.streamChunkSize),
327    );
328  }
329  if (options.strict) {
330    runArgs.push("--strict");
331  }
332
333  const runnerMode = (process.env.CLIO_RUNNER ?? "uv").trim().toLowerCase();
334  if (runnerMode === "python") {
335    const command =
336      (process.env.CLIO_PYTHON_BIN ?? "python").trim() || "python";
337    return {
338      command,
