476export async function startBackgroundRun(params: {
477  inputPath: string;
478  requestedRunId?: string | null;
479  options: Partial<RunLaunchOptions>;
480  runsRoot?: string;
481}): Promise<RunJobRecord> {
482  const runsRoot = params.runsRoot ?? getRunsRootPath();
483  await ensureDirectory(runsRoot);
484  await ensureDirectory(runJobsRoot(runsRoot));
485
486  const runIdCandidate = sanitizeRunId(params.requestedRunId ?? "");
487  const runId = runIdCandidate || generateRunId();
488  const runRoot = path.join(runsRoot, runId);
489  await ensureDirectory(runRoot);
490
491  const options = normalizeLaunchOptions(params.options);
492  const commandSpec = buildRunCommand({
493    runId,
494    inputPath: params.inputPath,
495    options,
496  });
497  const commandLine = [commandSpec.command, ...commandSpec.args];
498  const logPath = path.join(runRoot, "ui_run.log");
499  await fs.writeFile(
500    logPath,
501    `[${new Date().toISOString()}] Starting run ${runId}\n$ ${commandLine.join(" ")}\n\n`,
502    "utf-8",
503  );
504
505  const outFd = openSync(logPath, "a");
506  const child = spawn(commandSpec.command, commandSpec.args, {
507    cwd: projectRoot(),
508    env: process.env,
509    detached: true,
510    stdio: ["ignore", outFd, outFd],
511  });
512  closeSync(outFd);
513  child.unref();
514
515  if (!child.pid || child.pid <= 0) {
516    throw new Error("Failed to launch background run process.");
517  }
518
519  const payload: JsonRecord = {
520    runId,
521    runRoot,
522    pid: child.pid,
523    startedAtUtc: new Date().toISOString(),
524    inputPath: params.inputPath,
525    options,
526    command: commandLine,
527    logPath,
528  };
529  await writeJson(path.join(runJobsRoot(runsRoot), `${runId}.json`), payload);
530
531  const job = await deriveJobRecord(payload, runsRoot);
532  if (!job) {
533    throw new Error("Failed to build run job payload.");
534  }
535  return job;
536}
