538export async function collectRunJobs(
539  runsRoot = getRunsRootPath(),
540  limit = 120,
541): Promise<RunJobRecord[]> {
542  const jobsPath = runJobsRoot(runsRoot);
543  try {
544    const entries = await fs.readdir(jobsPath, { withFileTypes: true });
545    const files = entries
546      .filter((entry) => entry.isFile() && entry.name.endsWith(".json"))
547      .map((entry) => path.join(jobsPath, entry.name));
548    const payloads = await Promise.all(
549      files.map((filePath) => readJson(filePath)),
550    );
551    const jobs = await Promise.all(
552      payloads
553        .filter((payload): payload is JsonRecord => Boolean(payload))
554        .map((payload) => deriveJobRecord(payload, runsRoot)),
555    );
556
557    const filtered = jobs.filter((item): item is RunJobRecord => Boolean(item));
558    filtered.sort(
559      (a, b) => Date.parse(b.startedAtUtc) - Date.parse(a.startedAtUtc),
560    );
561    if (limit <= 0) {
562      return filtered;
563    }
564    return filtered.slice(0, limit);
565  } catch {
566    return [];
567  }
568}
