570export async function terminateRunJob(
571  runId: string,
572  runsRoot = getRunsRootPath(),
573): Promise<{ ok: boolean; status: string; job: RunJobRecord | null }> {
574  const terminatePid = (
575    pid: number,
576  ): { ok: boolean; status: string; terminatedPid: number | null } => {
577    if (pid <= 0) {
578      return { ok: false, status: "invalid_pid", terminatedPid: null };
579    }
580    if (!isPidRunning(pid)) {
581      return { ok: false, status: "process_not_running", terminatedPid: pid };
582    }
583
584    try {
585      process.kill(-pid, "SIGTERM");
586    } catch {
587      try {
588        process.kill(pid, "SIGTERM");
589      } catch (error) {
590        return {
591          ok: false,
592          status: error instanceof Error ? error.message : "terminate_failed",
593          terminatedPid: pid,
594        };
595      }
596    }
597    return { ok: true, status: "terminated", terminatedPid: pid };
598  };
599
600  const payload = await readJobPayloadByRunId(runId, runsRoot);
601  if (!payload) {
602    const runRoot = path.join(runsRoot, runId);
603    const lockState = await readRunLockState(runRoot);
604    if (lockState.lockOwnerPid && lockState.lockOwnerPid > 0) {
605      const lockTermination = terminatePid(lockState.lockOwnerPid);
606      return {
607        ok: lockTermination.ok,
608        status: lockTermination.ok
609          ? "terminated_lock_pid"
610          : lockTermination.status,
611        job: null,
612      };
613    }
614
615    return {
616      ok: false,
617      status: lockState.lockFilePresent
618        ? "lock_owner_unknown"
619        : "job_not_found",
620      job: null,
621    };
622  }
623
624  const job = await deriveJobRecord(payload, runsRoot);
625  if (!job) {
626    return { ok: false, status: "invalid_job_payload", job: null };
627  }
628
629  const termination = terminatePid(job.pid);
630  return {
631    ok: termination.ok,
632    status: termination.status,
633    job,
634  };
635}
