637export async function readRunJobLogTail(params: {
638  runId: string;
639  lines?: number;
640  runsRoot?: string;
641}): Promise<{
642  runId: string;
643  status: string;
644  running: boolean;
645  logPath: string;
646  lineCount: number;
647  logTail: string;
648}> {
649  const runsRoot = params.runsRoot ?? getRunsRootPath();
650  const lines = Math.max(20, Math.min(500, Math.floor(params.lines ?? 120)));
651  const payload = await readJobPayloadByRunId(params.runId, runsRoot);
652  const job = payload ? await deriveJobRecord(payload, runsRoot) : null;
653
654  const fallbackLogPath = path.join(runsRoot, params.runId, "ui_run.log");
655  const logPath = job?.logPath || fallbackLogPath;
656  try {
657    const raw = await fs.readFile(logPath, "utf-8");
658    const allLines = raw.split(/\r?\n/);
659    const trimmed = allLines.filter((line, index) => {
660      if (index === allLines.length - 1 && line === "") {
661        return false;
662      }
663      return true;
664    });
665    const tailLines = trimmed.slice(-lines);
666    return {
667      runId: params.runId,
668      status: job?.status ?? "unknown",
669      running: job?.running ?? false,
670      logPath,
671      lineCount: tailLines.length,
672      logTail: tailLines.join("\n"),
673    };
674  } catch {
675    return {
676      runId: params.runId,
677      status: job?.status ?? "unknown",
678      running: job?.running ?? false,
679      logPath,
680      lineCount: 0,
681      logTail: "",
682    };
683  }
684}
