 79type JsonRecord = Record<string, unknown>;
 80
 81function asRecord(value: unknown): JsonRecord | null {
 82  if (typeof value !== "object" || value === null || Array.isArray(value)) {
 83    return null;
 84  }
 85  return value as JsonRecord;
 86}
 87
 88function asArray(value: unknown): unknown[] {
 89  return Array.isArray(value) ? value : [];
 90}
 91
 92function asString(value: unknown): string {
 93  return typeof value === "string" ? value : "";
 94}
 95
 96function asStringArray(value: unknown): string[] {
 97  if (!Array.isArray(value)) {
 98    return [];
 99  }
100  return value.filter((item): item is string => typeof item === "string");
101}
102
103function asNodeIdArray(value: unknown): string[] {
104  if (!Array.isArray(value)) {
105    return [];
106  }
107  const output: string[] = [];
108  for (const item of value) {
109    if (typeof item === "string" && item.trim()) {
110      output.push(item);
111      continue;
112    }
113    if (typeof item === "number" && Number.isFinite(item)) {
114      output.push(String(Math.trunc(item)));
115    }
116  }
117  return output;
118}
119
120function asNumber(value: unknown, fallback = 0): number {
121  if (typeof value === "number" && Number.isFinite(value)) {
122    return value;
123  }
124  if (typeof value === "string" && value.trim().length > 0) {
125    const parsed = Number(value);
126    if (Number.isFinite(parsed)) {
127      return parsed;
128    }
129  }
130  return fallback;
131}
132
133function toNullableNumber(value: unknown): number | null {
134  const parsed = asNumber(value, Number.NaN);
135  return Number.isNaN(parsed) ? null : parsed;
136}
137
138function toRoundedPercent(processed: number, total: number): number {
139  if (!Number.isFinite(processed) || !Number.isFinite(total) || total <= 0) {
140    return 0;
141  }
142  const raw = (processed / total) * 100;
143  return Math.max(0, Math.min(100, Math.round(raw * 10) / 10));
144}
145
146function safeDateSortValue(value: string): number {
147  const timestamp = Date.parse(value);
148  return Number.isNaN(timestamp) ? 0 : timestamp;
149}
