635        runRoot,
636        manifest,
637        runMetrics,
638        runLockActive: runLockState.active,
639      });
640    }),
641  );
642
643  const filtered = rows.filter((item): item is RunListItem => Boolean(item));
644  filtered.sort((a, b) => {
645    const updated =
646      safeDateSortValue(b.updatedAtUtc) - safeDateSortValue(a.updatedAtUtc);
647    if (updated !== 0) {
648      return updated;
649    }
650    const created =
651      safeDateSortValue(b.createdAtUtc) - safeDateSortValue(a.createdAtUtc);
652    if (created !== 0) {
653      return created;
654    }
655    return b.runId.localeCompare(a.runId);
656  });
657
658  if (limit <= 0) {
659    return filtered;
660  }
661  return filtered.slice(0, limit);
662}
