 573      const formData = new FormData();
 574      if (requestedRunId.trim()) {
 575        formData.set("runId", requestedRunId.trim());
 576      }
 577      formData.set("file", selectedFile);
 578      formData.set("options", JSON.stringify(launchOptions));
 579
 580      const response = await fetch("/api/runs/launch", {
 581        method: "POST",
 582        body: formData,
 583      });
 584      const payload = (await response.json()) as RunLaunchResponse | JsonError;
 585      if (!response.ok) {
 586        if (typeof payload === "object" && payload && "error" in payload) {
 587          throw new Error(payload.error);
 588        }
 589        throw new Error(`Run launch failed (${response.status})`);
 590      }
 591
 592      const launchPayload = payload as RunLaunchResponse;
