 594        description: launchPayload.job.runId,
 595      });
 596      onRunStarted(launchPayload.job.runId);
 597      setSelectedLogRunId(launchPayload.job.runId);
 598      setRequestedRunId("");
 599      setSelectedFile(null);
 600      await Promise.all([loadJobs(), loadLogTail()]);
 601    } catch (error) {
