 643          const status =
 644            typeof payload === "object" && payload && "status" in payload
 645              ? payload.status
 646              : `HTTP ${response.status}`;
 647          throw new Error(`Could not terminate run ${runId}: ${status}`);
 648        }
 649        const terminatePayload = payload as RunTerminateResponse;
 650        if (terminatePayload.ok) {
