 13type JsonRecord = Record<string, unknown>;
 14
 15function asRecord(value: unknown): JsonRecord | null {
 16  if (typeof value !== "object" || value === null || Array.isArray(value)) {
 17    return null;
 18  }
 19  return value as JsonRecord;
 20}
 21
 22function asString(value: unknown): string {
 23  return typeof value === "string" ? value : "";
 24}
 25
 26function asStringArray(value: unknown): string[] {
 27  if (!Array.isArray(value)) {
 28    return [];
 29  }
 30  return value.filter((item): item is string => typeof item === "string");
 31}
 32
 33function asNumber(value: unknown, fallback = 0): number {
 34  if (typeof value === "number" && Number.isFinite(value)) {
 35    return value;
 36  }
 37  if (typeof value === "string" && value.trim()) {
 38    const parsed = Number(value);
 39    if (Number.isFinite(parsed)) {
 40      return parsed;
 41    }
 42  }
 43  return fallback;
 44}
 45
 46function utcStamp(): string {
 47  return new Date()
 48    .toISOString()
 49    .replace(/[-:]/g, "")
 50    .replace(/\.\d{3}Z$/, "Z");
 51}
