 69async function readJson(filePath: string): Promise<JsonRecord | null> {
 70  try {
 71    const raw = await fs.readFile(filePath, "utf-8");
 72    return asRecord(JSON.parse(raw));
 73  } catch {
 74    return null;
 75  }
 76}
