 92function isPidRunning(pid: number): boolean {
 93  if (!Number.isFinite(pid) || pid <= 0) {
 94    return false;
 95  }
 96  try {
 97    process.kill(pid, 0);
 98    return true;
 99  } catch {
100    return false;
101  }
102}
