115async function readRunLockState(runRoot: string): Promise<{
116  active: boolean;
117  lockFilePresent: boolean;
118  lockOwnerPid: number | null;
119}> {
120  const lockPath = path.join(runRoot, ".run.lock");
121  const lockPayload = await readJson(lockPath);
122  const lockFilePresent = lockPayload !== null || (await fileExists(lockPath));
123  if (!lockFilePresent) {
124    return {
125      active: false,
126      lockFilePresent: false,
127      lockOwnerPid: null,
128    };
129  }
130
131  const pid = asNumber(lockPayload?.pid, 0);
132  if (pid > 0) {
133    return {
134      active: isPidRunning(pid),
135      lockFilePresent: true,
136      lockOwnerPid: pid,
137    };
138  }
139  return {
140    active: true,
141    lockFilePresent: true,
142    lockOwnerPid: null,
143  };
144}
