146function generateRunId(size = 12): string {
147  let output = "";
148  for (let index = 0; index < size; index += 1) {
149    output += RUN_ID_ALPHABET[randomInt(0, RUN_ID_ALPHABET.length)];
150  }
151  return output;
152}
