162function normalizeLaunchOptions(
163  options: Partial<RunLaunchOptions>,
164): RunLaunchOptions {
165  const withHierarchy = Boolean(options.withHierarchy);
166  const withPrivacy = Boolean(options.withPrivacy);
167  const withLabeling = Boolean(
168    options.withLabeling || withHierarchy || withPrivacy,
169  );
170  const withClustering = Boolean(options.withClustering || withLabeling);
171  const withFacets = Boolean(options.withFacets || withClustering);
172
173  const streamChunkSizeRaw = asNumber(options.streamChunkSize, 32);
174  const streamChunkSize = Math.max(1, Math.floor(streamChunkSizeRaw));
175  const hierarchyLevelsRaw = asNumber(options.hierarchyLevels, 5);
176  const hierarchyLevels = Math.max(
177    2,
178    Math.min(20, Math.floor(hierarchyLevelsRaw)),
179  );
180  const hierarchyDepthPolicy =
181    options.hierarchyDepthPolicy === "strict_min" ? "strict_min" : "adaptive";
182  const clusterStrategy =
183    options.clusterStrategy === "kmeans" ||
184    options.clusterStrategy === "hdbscan"
185      ? options.clusterStrategy
186      : "hybrid";
187  const clusterLeafMode =
188    options.clusterLeafMode === "fixed" ? "fixed" : "auto";
189  const clusterTargetLeafSize = Math.max(
190    1,
191    Math.floor(asNumber(options.clusterTargetLeafSize, 25)),
192  );
193  const clusterMinLeafClusters = Math.max(
194    1,
195    Math.floor(asNumber(options.clusterMinLeafClusters, 20)),
196  );
197  const clusterMaxLeafClusters = Math.max(
198    clusterMinLeafClusters,
199    Math.floor(asNumber(options.clusterMaxLeafClusters, 600)),
200  );
201  const clusterHdbscanMinClusterSize = Math.max(
202    2,
203    Math.floor(asNumber(options.clusterHdbscanMinClusterSize, 12)),
204  );
205  const clusterHdbscanMinSamples = Math.max(
206    1,
207    Math.floor(asNumber(options.clusterHdbscanMinSamples, 6)),
208  );
209  const clusterNoisePolicy =
210    options.clusterNoisePolicy === "singleton" ||
211    options.clusterNoisePolicy === "drop"
212      ? options.clusterNoisePolicy
213      : "nearest";
214
215  const limitRaw = asNumber(options.limit, 0);
216  const evalCountRaw = asNumber(options.evalCount, 0);
217
218  return {
219    withFacets,
220    withClustering,
221    withLabeling,
222    withHierarchy,
223    withPrivacy,
224    withEval: Boolean(options.withEval),
225    streaming: Boolean(options.streaming),
226    streamChunkSize,
227    hierarchyLevels,
228    hierarchyDepthPolicy,
229    clusterStrategy,
230    clusterLeafMode,
231    clusterTargetLeafSize,
232    clusterMinLeafClusters,
233    clusterMaxLeafClusters,
234    clusterHdbscanMinClusterSize,
235    clusterHdbscanMinSamples,
236    clusterNoisePolicy,
237    strict: Boolean(options.strict),
238    limit: limitRaw > 0 ? Math.floor(limitRaw) : null,
239    evalCount: evalCountRaw > 0 ? Math.floor(evalCountRaw) : null,
240  };
241}
