361async function deriveJobRecord(
362  payload: JsonRecord,
363  runsRoot: string,
364): Promise<RunJobRecord | null> {
365  const runIdCandidate = asString(payload.runId) || asString(payload.run_id);
366  const runRootCandidate =
367    asString(payload.runRoot) || asString(payload.run_root);
368  const fallbackRunId =
369    runIdCandidate || path.basename(runRootCandidate || "") || "";
370  const runRootFallback = path.join(runsRoot, fallbackRunId);
371  const runRootRaw = runRootCandidate || runRootFallback;
372  const runRoot = resolvePathFromProject(runRootRaw, runRootFallback);
373  const runId = runIdCandidate || path.basename(runRoot);
374  if (!runId) {
375    return null;
376  }
377
378  const pid = asNumber(payload.pid, 0);
379  const startedAtUtc =
380    asString(payload.startedAtUtc) ||
381    asString(payload.started_at_utc) ||
382    new Date().toISOString();
383  const inputPath =
384    asString(payload.inputPath) || asString(payload.input_path) || "n/a";
385  const rawLogPath =
386    asString(payload.logPath) ||
387    asString(payload.log_path) ||
388    path.join(runRoot, "ui_run.log");
389  const logPath = resolvePathFromProject(
390    rawLogPath,
391    path.join(runRoot, "ui_run.log"),
392  );
393  const command = asStringArray(payload.command);
394
395  const lockState = await readRunLockState(runRoot);
396  const hasMetrics = await fileExists(path.join(runRoot, "run_metrics.json"));
397  const hasWarnings = await fileExists(path.join(runRoot, "run_warnings.json"));
398  const running = isPidRunning(pid) || lockState.active;
399
400  let status: RunJobRecord["status"] = running ? "running" : "finished";
401  if (!running && hasMetrics && hasWarnings) {
402    status = "finished_with_warnings";
403  } else if (!running && hasMetrics) {
404    status = "finished_ok";
405  }
406
407  return {
408    runId,
409    runRoot,
410    pid,
411    status,
412    running,
413    hasMetrics,
414    hasWarnings,
415    startedAtUtc,
416    inputPath,
417    logPath,
418    command,
419  };
420}
