448export async function persistUploadedInput(
449  file: File,
450  runsRoot = getRunsRootPath(),
451): Promise<{ inputPath: string; sizeBytes: number }> {
452  const bytes = Buffer.from(await file.arrayBuffer());
453  if (bytes.byteLength <= 0) {
454    throw new Error("Uploaded file is empty.");
455  }
456  if (bytes.byteLength > MAX_UPLOAD_BYTES) {
457    const limitMB = Math.round(MAX_UPLOAD_BYTES / (1024 * 1024));
458    throw new Error(`Uploaded file exceeds ${limitMB} MB limit.`);
459  }
460
461  await ensureDirectory(uploadsRoot(runsRoot));
462  const safeName = path
463    .basename(file.name || "uploaded_input.jsonl")
464    .replace(/\s+/g, "_");
465  const outputPath = path.join(
466    uploadsRoot(runsRoot),
467    `${utcStamp()}_${safeName}`,
468  );
469  await fs.writeFile(outputPath, bytes);
470  return {
471    inputPath: outputPath,
472    sizeBytes: bytes.byteLength,
473  };
474}
