const loadLogTail = useCallback(
  async (signal?: AbortSignal) => {
    if (!selectedLogRunId) {
      setLogData(null);
      setLogError("");
      return;
    }

    setLogLoading(true);
    try {
      const payload = await fetchJson<RunLogResponse>(
        `/api/runs/jobs/${encodeURIComponent(selectedLogRunId)}/logs?lines=180`,
        signal,
